import React from "react";
import NavBar from '../Components/navbar';
import "../Pages/pagestyle.css";

class CSGO extends React.Component {
  constructor(props){
    super(props);
    this.state= { 
      mynames: [],
      mylinks: [],
      friendnames: [],
      friendlinks: [],
      inputlink: null,
      inputname: null
    };
  }

  handleSubmit = (event) =>{
    event.preventDefault();
  }

  callAPI(){
    fetch("http://localhost:9000/csgoapi")
      .then(res => res.json())
      .then(res => this.setState({
        mynames: JSON.parse(res.mypfnames),
        mylinks: JSON.parse(res.mypflinks),
        friendnames: JSON.parse(res.friendpfnames),
        friendlinks: JSON.parse(res.friendpflinks)
      }));
  }

  componentDidMount(){
    this.callAPI();
  }

  render() {
    return (
      <div>
          <NavBar/>
          <div class="csgo">
              <div class="row">
                  <div class="column" style={{backgroundColor: 'rgba(63, 63, 191, 0.5)'}}>
                      <font color="white" size="10"> Add a profile </font>
                      <form class="submissionform">
                        <div class="form-group">
                            <label class='textcolor'>Link to profile</label>
                            <input type="url" class="form-control"  placeholder="Enter profile link" name="inputlink"/>
                        </div>
                        <div class="form-group">
                            <label class='textcolor'>Username</label>
                            <input type="text" class="form-control" placeholder="Enter username" name="inputname"/>
                        </div>
                        <div class="form-group" onSubmit={this.handleSubmit}>
                            <label class='textcolor'>Profile Type</label>
                            <select class="form-control">
  					                     <option>Choose profile type</option>
  					                     <option>Personal</option>
  					                     <option>Friends</option>
			  	                  </select>
			                  </div>
                        <button type="submit" class="btn btn-primary">Submit</button>
                      </form>
                  </div>
                  <div class="column" style={{backgroundColor: 'rgba(52, 52, 52, 0.5)'}}>
                      <div>
                          <font color="white" size="7"> Your Profile: </font>
                      </div>
                      <div>
                           <font size="5"><a href={this.state.mylinks[0]} style = {{color:'red'}}>{this.state.mynames[0]}</a></font>
                      </div>
                      <div>
                          <font color="white" size="7"> Friend Profiles: </font>
                      </div>
                      <div>
                          <font size="5"><a href={this.state.friendlinks[0]} style = {{color: 'red'}} >{this.state.friendnames[0]}</a></font>
                      </div>
                  </div>
              </div>
          </div>
     </div>
    );
  }
}

export default CSGO;
