import React from 'react';
import NavBar from '../Components/navbar';
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

const content = [
{
	Title: 'Fortnite',
	description: '',
	image: '../Images/fortnitebackground.jpg'
},
{
	Title: 'Overwatch',
	description: '',
	image: '../Images/owbackground.jpg'
},
{
	Title: 'Fortnite',
	description: '',
	image: '../Images/fortnitebackground.jpg'
},
{
	Title: 'League of Legends',
	description: '',
	image: '../Images/lolbackground.jpg'
},
{
	Title: 'Teamfight Tactics',
	description: '',
	image: '../Images/tftbackground.jpg'
},
{
	Title: 'CSGO',
	description: '',
	image: '../Images/csgobackground.jpg'
},
{
	Title: 'Osu!',
	description: '',
	image: '../Images/osubackground.png'
},
{
	Title: 'Rainbow 6 Seige',
	description: '',
	image: '../Images/r6background.jpg'
},
];
function Home() {
  return (
  	<div>
  	  <NavBar/>
      <Slider className="slider-wrapper">
      	{content.map((item, index) => <div key={index} className="slider-content" style={{ background: `url('${item.image}') no-repeat center center` }}>
    	<h2>{item.title}</h2>
    	<div>{item.description}</div>
  		</div>)}
      </Slider>
    </div>
  );
}

export default Home;
